import React, { useEffect, useState } from 'react';

export default function ProductModal({ open, mode, initialProduct, onClose, onSubmit }) {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [rating, setRating] = useState('');
  const [image, setImage] = useState('');

  const categories = [
    'Ноутбуки',
    'Смартфоны', 
    'Планшеты',
    'Мониторы',
    'Комплектующие',
    'Аксессуары',
    'Оргтехника',
    'Сетевое оборудование'
  ];

  // Заполняем форму при открытии/изменении initialProduct
  useEffect(() => {
    if (open) {
      setName(initialProduct?.name || '');
      setCategory(initialProduct?.category || categories[0]);
      setDescription(initialProduct?.description || '');
      setPrice(initialProduct?.price?.toString() || '');
      setStock(initialProduct?.stock?.toString() || '');
      setRating(initialProduct?.rating?.toString() || '');
      setImage(initialProduct?.image || '');
    }
  }, [open, initialProduct, categories]);

  // Если модалка закрыта, не рендерим ничего
  if (!open) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Валидация
    if (!name.trim()) {
      alert('Введите название товара');
      return;
    }
    
    if (!category) {
      alert('Выберите категорию');
      return;
    }
    
    if (!description.trim()) {
      alert('Введите описание товара');
      return;
    }
    
    const priceNum = Number(price);
    if (isNaN(priceNum) || priceNum <= 0) {
      alert('Введите корректную цену (больше 0)');
      return;
    }
    
    const stockNum = Number(stock);
    if (isNaN(stockNum) || stockNum < 0) {
      alert('Введите корректное количество (0 или больше)');
      return;
    }
    
    const ratingNum = rating ? Number(rating) : null;
    if (ratingNum && (ratingNum < 0 || ratingNum > 5)) {
      alert('Рейтинг должен быть от 0 до 5');
      return;
    }

    onSubmit({
      id: initialProduct?.id,
      name: name.trim(),
      category,
      description: description.trim(),
      price: priceNum,
      stock: stockNum,
      rating: ratingNum,
      image: image.trim() || 'https://via.placeholder.com/300'
    });
  };

  return (
    <div className="backdrop" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal__header">
          <h2 className="modal__title">
            {mode === 'edit' ? 'Редактирование товара' : 'Добавление товара'}
          </h2>
          <button className="iconBtn" onClick={onClose}>✕</button>
        </div>

        <form className="form" onSubmit={handleSubmit}>
          <label className="label">
            Название товара *
            <input
              type="text"
              className="input"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Например, Ноутбук Asus ROG"
              autoFocus
              required
            />
          </label>

          <label className="label">
            Категория *
            <select
              className="select"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Выберите категорию</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </label>

          <label className="label">
            Описание *
            <textarea
              className="textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Краткое описание товара..."
              required
            />
          </label>

          <div className="row">
            <label className="label">
              Цена (₽) *
              <input
                type="number"
                className="input"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                placeholder="1000"
                min="0"
                step="1"
                required
              />
            </label>

            <label className="label">
              На складе (шт.) *
              <input
                type="number"
                className="input"
                value={stock}
                onChange={(e) => setStock(e.target.value)}
                placeholder="10"
                min="0"
                step="1"
                required
              />
            </label>
          </div>

          <div className="row">
            <label className="label">
              Рейтинг (0-5)
              <input
                type="number"
                className="input"
                value={rating}
                onChange={(e) => setRating(e.target.value)}
                placeholder="4.5"
                min="0"
                max="5"
                step="0.1"
              />
            </label>

            <label className="label">
              URL фото
              <input
                type="text"
                className="input"
                value={image}
                onChange={(e) => setImage(e.target.value)}
                placeholder="https://example.com/photo.jpg"
              />
            </label>
          </div>

          <div className="modal__footer">
            <button type="button" className="btn" onClick={onClose}>
              Отмена
            </button>
            <button type="submit" className="btn btn--primary">
              {mode === 'edit' ? 'Сохранить' : 'Создать'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
