import React, { useEffect, useState } from 'react';

export default function ProductModal({ open, mode, initialProduct, onClose, onSubmit }) {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [rating, setRating] = useState('');
  const [image, setImage] = useState('');

  const categories = [
    'Ноутбуки',
    'Смартфоны',
    'Планшеты',
    'Мониторы',
    'Комплектующие',
    'Аксессуары',
    'Оргтехника',
    'Сетевое оборудование'
  ];

  useEffect(() => {
    if (!open) return;
    
    setName(initialProduct?.name ?? '');
    setCategory(initialProduct?.category ?? categories[0]);
    setDescription(initialProduct?.description ?? '');
    setPrice(initialProduct?.price != null ? String(initialProduct.price) : '');
    setStock(initialProduct?.stock != null ? String(initialProduct.stock) : '');
    setRating(initialProduct?.rating != null ? String(initialProduct.rating) : '');
    setImage(initialProduct?.image ?? '');
  }, [open, initialProduct, categories]);

  if (!open) return null;

  const title = mode === 'edit' ? 'Редактирование товара' : 'Добавление товара';

  const handleSubmit = (e) => {
    e.preventDefault();

    const trimmedName = name.trim();
    const trimmedDesc = description.trim();
    const parsedPrice = Number(price);
    const parsedStock = Number(stock);
    const parsedRating = rating ? Number(rating) : null;

    if (!trimmedName) {
      alert('Введите название товара');
      return;
    }

    if (!category) {
      alert('Выберите категорию');
      return;
    }

    if (!trimmedDesc) {
      alert('Введите описание товара');
      return;
    }

    if (!Number.isFinite(parsedPrice) || parsedPrice <= 0) {
      alert('Введите корректную цену (больше 0)');
      return;
    }

    if (!Number.isFinite(parsedStock) || parsedStock < 0) {
      alert('Введите корректное количество на складе (0 или больше)');
      return;
    }

    if (parsedRating && (parsedRating < 0 || parsedRating > 5)) {
      alert('Рейтинг должен быть от 0 до 5');
      return;
    }

    onSubmit({
      id: initialProduct?.id,
      name: trimmedName,
      category,
      description: trimmedDesc,
      price: parsedPrice,
      stock: parsedStock,
      rating: parsedRating,
      image: image.trim() || 'https://via.placeholder.com/300x200?text=Product'
    });
  };

  return (
    <div className="backdrop" onMouseDown={onClose}>
      <div 
        className="modal" 
        onMouseDown={(e) => e.stopPropagation()}
        role="dialog"
        aria-modal="true"
      >
        <div className="modal__header">
          <div className="modal__title">{title}</div>
          <button className="iconBtn" onClick={onClose} aria-label="Закрыть">
            ✕
          </button>
        </div>

        <form className="form" onSubmit={handleSubmit}>
          <label className="label">
            Название товара *
            <input
              className="input"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Например, Ноутбук Asus ROG"
              autoFocus
              required
            />
          </label>

          <label className="label">
            Категория *
            <select
              className="select"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Выберите категорию</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </label>

          <label className="label">
            Описание *
            <textarea
              className="textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Краткое описание товара..."
              required
            />
          </label>

          <div className="row">
            <label className="label">
              Цена (₽) *
              <input
                className="input"
                type="number"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                placeholder="1000"
                min="0"
                step="1"
                required
              />
            </label>

            <label className="label">
              На складе (шт.) *
              <input
                className="input"
                type="number"
                value={stock}
                onChange={(e) => setStock(e.target.value)}
                placeholder="10"
                min="0"
                step="1"
                required
              />
            </label>
          </div>

          <div className="row">
            <label className="label">
              Рейтинг (0-5)
              <input
                className="input"
                type="number"
                value={rating}
                onChange={(e) => setRating(e.target.value)}
                placeholder="4.5"
                min="0"
                max="5"
                step="0.1"
              />
            </label>

            <label className="label">
              URL фото
              <input
                className="input"
                value={image}
                onChange={(e) => setImage(e.target.value)}
                placeholder="https://example.com/photo.jpg"
              />
            </label>
          </div>

          <div className="modal__footer">
            <button type="button" className="btn" onClick={onClose}>
              Отмена
            </button>
            <button type="submit" className="btn btn--primary">
              {mode === 'edit' ? 'Сохранить' : 'Создать'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
