import React, { useEffect, useState } from 'react';

export default function ProductModal({ open, mode, initialProduct, onClose, onSubmit }) {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [rating, setRating] = useState('');
  const [image, setImage] = useState('');

  const categories = ['Ноутбуки', 'Смартфоны', 'Планшеты', 'Мониторы', 'Комплектующие', 'Аксессуары'];

  useEffect(() => {
    if (open) {
      setName(initialProduct?.name || '');
      setCategory(initialProduct?.category || categories[0]);
      setDescription(initialProduct?.description || '');
      setPrice(initialProduct?.price?.toString() || '');
      setStock(initialProduct?.stock?.toString() || '');
      setRating(initialProduct?.rating?.toString() || '');
      setImage(initialProduct?.image || '');
    }
  }, [open, initialProduct]);

  if (!open) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      id: initialProduct?.id,
      name: name.trim(),
      category,
      description: description.trim(),
      price: Number(price),
      stock: Number(stock),
      rating: rating ? Number(rating) : null,
      image
    });
  };

  return (
    <div className="backdrop" onClick={onClose}>
      <div className="modal" onClick={e => e.stopPropagation()}>
        <div className="modal__header">
          <h2>{mode === 'edit' ? 'Редактировать' : 'Создать'} товар</h2>
          <button onClick={onClose}>&times;</button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Название:</label>
            <input 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              required 
            />
          </div>

          <div className="form-group">
            <label>Категория:</label>
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
              {categories.map(c => <option key={c}>{c}</option>)}
            </select>
          </div>

          <div className="form-group">
            <label>Описание:</label>
            <textarea 
              value={description} 
              onChange={(e) => setDescription(e.target.value)} 
              required 
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Цена (₽):</label>
              <input 
                type="number" 
                value={price} 
                onChange={(e) => setPrice(e.target.value)} 
                required 
              />
            </div>

            <div className="form-group">
              <label>Количество:</label>
              <input 
                type="number" 
                value={stock} 
                onChange={(e) => setStock(e.target.value)} 
                required 
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Рейтинг (0-5):</label>
              <input 
                type="number" 
                step="0.1" 
                min="0" 
                max="5" 
                value={rating} 
                onChange={(e) => setRating(e.target.value)} 
              />
            </div>

            <div className="form-group">
              <label>URL фото:</label>
              <input 
                value={image} 
                onChange={(e) => setImage(e.target.value)} 
              />
            </div>
          </div>

          <div className="modal__footer">
            <button type="button" onClick={onClose}>Отмена</button>
            <button type="submit">
              {mode === 'edit' ? 'Сохранить' : 'Создать'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
